```javascript
function moveScrollY(targetY, speed) {
  const scrollY = window.scrollY || window.pageYOffset;
  const t = Math.min(0.5, Math.abs((targetY - scrollY) / speed)); //  0.5(500ms) 
  let currentTime = 0;

  function move() {
    currentTime += 1 / 60;
    var p = currentTime / t;
    var d = Math.cos(Math.PI * pos) - 1); //  cos 

    if (p < 1) {
      scrollTo(0, scrollY + (targetY - scrollY) * d);
      requestAnimationFrame(move); //  requestAnimationFrame
    } else {
      scrollTo(0, targetY);
      alert('done');
    }
  }
  move();
}
```

